@Component({
    selector: 'app-r',
    templateUrl: './header/header.component.html',
    // template:`<p>Lorem ipsum dolor sit charan kiran adipisicing elit. Saepe, quasi?</p>`,
    styleUrls: ['./app.component.css']
})
export class AppComponent {
    title = 'nice framework';
}
<div class="header">
    <div class="site-image">
        <img [src]="source" height= "240px" width="320px" alt="">
    </div>
    <div class="site-slogan">
        <h2>{{getslogan()}}</h2>
    </div>
</div>
